import {useContext, useEffect,useState} from 'react';
import {CartContext} from '../pages/CartContext';
import pizzaData from '../components/PizzaData.json';
import {getAllPizzas , getPizzaById} from '../helper';


const Cart = () => {
  const [products, setProducts] = useState([]);
  const { cart } = useContext(CartContext);
  const [priceFetched, togglePriceFetched] = useState(false);

  const fetchPizzaData = () => {
    if (!cart.items) {
      return;
    }

    if (priceFetched) {
      return;
    }

    const productIds = Object.keys(cart.items);
    const fetchedProducts = productIds.map(productId => {
      return getPizzaById(productId, pizzaData);
    });
    setProducts(fetchedProducts);
    togglePriceFetched(true);
  };

  useEffect(() => {
    fetchPizzaData();
  }, [cart, priceFetched]);
  return (
    <div  className="container mx-auto lg:w-1/2 w-full pb-24"> 
        <h1 className="my-12 font-bold">Cart items</h1>
        <ul>
          <li className="mb-12">
            <div className="flex items-center justify-between">
                 <div className="flex items-center" >
                    <img  className="h-16" src="data:image/jpeg;base64," alt="pizza" />
                    <span className="font-bold ml-4 w-48">Double peproni</span>
                 </div>
                 <div>
                  <button className="bg-yellow-500 px-4 py-2 rounded-full leading-none">-</button>
                  <b className="px-4">2</b>
                  <button className="bg-yellow-500 px-4 py-2 rounded-full leading-none">+</button>
                 </div>
                 <span>₹ 500</span>
                 <button className="bg-red-500 px-4 py-2 rounded-full leading-none text-white">Delete</button>
            </div>
          </li>
          <li className="mb-12"> 
            <div className="flex items-center justify-between">
                 <div className="flex items-center" >
                    <img  className="h-16" src="data:image/jpeg;base64," alt="pizza" />
                    <span className="font-bold ml-4 w-48">Double peproni</span>
                 </div>
                 <div>
                  <button className="bg-yellow-500 px-4 py-2 rounded-full leading-none">-</button>
                  <b className="px-4">2</b>
                  <button className="bg-yellow-500 px-4 py-2 rounded-full leading-none">+</button>
                 </div>
                 <span>₹ 500</span>
                 <button className="bg-red-500 px-4 py-2 rounded-full leading-none text-white">Delete</button>
            </div>
          </li>
        </ul>
        <hr className="my-6"/>
        <div className="text-right ">
         <b> Grand Total:</b> ₹ 1000 
        </div>
        <div className="text-right mt-6">
          <button className="bg-yellow-500 px-4 py-2 rounded-full leading-none">Order Now</button>
        </div>
    </div>
  )
}

export default Cart;